import React from 'react';
import {View, Text, Image, StyleSheet, Picker} from 'react-native';
import AppIntroSlider from 'react-native-app-intro-slider';

const data = [
  {
    title: 'Informacion del ventilador',
    text: 'Por favor agregue los campos requeridos para realizar un adecuado procesamiento de datos',
    image: require('./assets/oxygen-mask.png'),
    bg: '#febe29',
  },
  {
    title: 'Rocket guy',
    text: "I'm already out of descriptions\n\nLorem ipsum bla bla bla",
    image: 'https://images.assetsdelivery.com/compings_v2/bezwershenko/bezwershenko1810/bezwershenko181000003.jpg',
    bg: '#22bcb5',
  },
];

const styles = StyleSheet.create({
  slide: {
    padding:18,
    alignItems: 'center',
  },
  image: {
    width: 150,
    height: 150,
    marginVertical: 50,
  },
  text: {
    color: 'rgba(255, 255, 255, 0.8)',
    textAlign: 'center',
  },
  title: {
    fontSize: 28,
    color: 'white',
    textAlign: 'center',
  },
});

export default class App extends React.Component {
  _renderItem = ({item}) => {
    return (
      <View
        style={[
          styles.slide,

        ]}>
        <Text style={styles.title}>{item.title}</Text>
        <Image source={{ uri: item.image}} style={styles.image} />
        <Text style={styles.text}>{item.text}</Text>
        <Picker
        selectedValue={selectedValue}
        style={{ height: 50, width: 150 }}
      >
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
      </Picker>
      </View>
    );
  };

  _keyExtractor = (item) => item.title;

  render() {
    return (
        <AppIntroSlider
          style={{backgroundColor:'#22bcb5'}}
          keyExtractor={this._keyExtractor}
          renderItem={this._renderItem}
          data={data}
        />
    );
  }
}
