import React from 'react';
import { View, Text, Image, StyleSheet, Picker } from 'react-native';
import AppIntroSlider from 'react-native-app-intro-slider';
import {} from './components/data/data'

const styles = StyleSheet.create({
  slide: {
    padding: 18,
    alignItems: 'center',
  },
  image: {
    width: 120,
    height: 120,
    marginVertical: 50,
  },
  text: {
    fontSize: 16,
    justifyContent: 'center',
    color: 'rgba(255, 255, 255, 0.8)',
    textAlign: 'center',
  },
  title: {
    fontSize: 30,
    color: 'white',
    textAlign: 'center',
  },
  container: {
    paddingTop: 40,
    alignItems: "center"
  }
});

export default class App extends React.Component {

  _renderItem = ({ item }) => {
    console.log(item)
    return (
      <View
        style={[
          styles.slide
        ]}>
        <Text style={styles.title}>{item.title}</Text>
        <Image source={{ uri: item.image }} style={styles.image} />
        <Text style={styles.text}>{item.text}</Text>

        {item.options.map(option =>

          <View style={{ flex: 1, flexDirection: 'row', alignContent: 'stretch', margin: 30 }}>
            <Text style={[styles.text], { width: 80, color: 'white' }}>{option.type}</Text>
            <Picker
              style={{ height: 30, width: 200 }}
            >
              <Picker.Item style={{ fontSize: 10 }} label="Invasivo" value="java" />
              <Picker.Item style={{ fontSize: 10 }} label="No invasivo" value="js" />
            </Picker>
          </View>
        )}

      </View>
    );
  };

  _keyExtractor = (item) => item.title;

  render() {
    return (
      <AppIntroSlider
        style={{ backgroundColor: '#22bcb5' }}
        keyExtractor={this._keyExtractor}
        renderItem={this._renderItem}
        data={data}
      />
    );
  }
}
